#!/bin/bash
yum install -y wget python-devel gcc iperf3;
pip install python-daemon==2.1.2; pip install psutil; pip install Kafka-python; mkdir -p /var/private/ssl;git clone https://github.com/RENCI-NRIG/host-key-tools.git /root/host-key-tools
cd /root/host-key-tools/hostkey-py/
python setup.py install
cp /root/host-key-tools/ch-host-key-tools.service /usr/lib/systemd/system/host-key-tools.service
sed -i 's/ExecStart=.*/ExecStart=\/usr\/bin\/hostkeyd start -c https:\/\/comet-hn1.exogeni.net:8111\/ -s WORKFLOW -r WORKFLOWread -w WORKFLOWwrite -k dynamo-broker1.exogeni.net:9093 -t KAFKATOPIC/g' /usr/lib/systemd/system/host-key-tools.service
systemctl enable host-key-tools.service
cd /root/; wget  http://geni-images.renci.org/images/kthare10/certs/DigiCertCA.crt -O /var/private/ssl/ca.crt; wget  http://geni-images.renci.org/images/kthare10/certs/inno-hn_exogeni_net.pem -O /var/private/ssl/client.pem; wget  http://geni-images.renci.org/images/kthare10/certs/inno-hn_exogeni_net.key -O /var/private/ssl/key.pem ;
service host-key-tools.service start
